function hasClass(b,a){return b.className.match(new RegExp("(\\s|^)"+a+"(\\s|$)"));}function addClass(b,a){if(!this.hasClass(b,a)){b.className+=" "+a;}}function removeClass(c,a){if(hasClass(c,a)){var b=new RegExp("(\\s|^)"+a+"(\\s|$)");
c.className=c.className.replace(b," ");}}function replaceClass(b,a,c){if(hasClass(b,a)){removeClass(b,a);addClass(b,c);}return;}function toggleClass(c,b,a){if(hasClass(c,b)){replaceClass(c,b,a);
}else{if(hasClass(c,a)){replaceClass(c,a,b);}else{addClass(c,b);}}}